import React, { useState, useRef, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import osm from './osm-provider';
import "leaflet/dist/leaflet.css";

function LocationComponent({ setCenter }) {
    const getLocation = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                position => {
                    const { latitude, longitude } = position.coords;
                    setCenter({ lat: latitude, lng: longitude });
                },
                error => {
                    console.error('Error getting location:', error.message);
                }
            );
        } else {
            console.error('Geolocation is not supported by this browser.');
        }
    };

    useEffect(() => {
        getLocation();
    }, []);

    return null; // LocationComponent doesn't render anything
}

function Maps() {
    const [center, setCenter] = useState(null);
    const [markers, setMarkers] = useState([]);
    const ZOOM = 9;
    const mapRef = useRef();

    const addMarker = (e) => {
        console.log("Map clicked!", e);  // Log to see if this function is called
        const newMarker = e.latlng;
        console.log("New marker:", newMarker);  // Log the position of the new marker
        setMarkers(currentMarkers => [...currentMarkers, newMarker]);
    };

    return (
        <div className='container'>
            <LocationComponent setCenter={setCenter} />
            {center && (
                <MapContainer center={center} zoom={ZOOM} ref={mapRef} whenCreated={mapInstance => mapRef.current = mapInstance} onClick={addMarker}>
                    <TileLayer url={osm.maptiler.url} attribution={osm.maptiler.attribution} />
                    {markers.map((marker, idx) => (
                        <Marker key={idx} position={marker}>
                            <Popup>
                                Bee found here! Latitude: {marker.lat.toFixed(3)}, Longitude: {marker.lng.toFixed(3)}
                            </Popup>
                        </Marker>
                    ))}
                </MapContainer>
            )}
        </div>
    );
}


export default Maps;
