import React from 'react';
import { useParams } from 'react-router-dom';
import category from './beecategory.json';
import InputForm from './Input';
import './Details.css';

function Details() {
    const { beeId } = useParams();
    const bee = category.find(b => b.id.toString() === beeId);

    return (
        <div className="details-container"> 
            {bee ? (
                <div className="bee-details">
                    <h1 className="bee-name">{bee.name}</h1>
                    <img src={bee.imageUrl} alt={bee.name} className="bee-image" />
                    <p className="bee-description">{bee.description}</p>
                    <InputForm selectedBeeName={bee.name} className="InputForm" />
                </div>
            ) : (
                <p className="no-bee">No bee found.</p>
            )}
        </div>
    );
}


export default Details;
