import React, { useState, useRef, useEffect } from 'react';
import { MapContainer, Marker, Popup, TileLayer } from 'react-leaflet';
import osm from './osm-provider';
import "leaflet/dist/leaflet.css";
import L from 'leaflet';
import bees from './BeeData.json';
import useGeoLocation from './useGeoLocation';

const markerIcon = new L.Icon({
    iconUrl: '../images/beeicon.png',
    iconSize: [35, 45],
    iconAnchor: [17, 46],
    popupAnchor: [0, -46],
});

const personIcon = new L.Icon({
    iconUrl: '../images/561237.png',
    iconSize: [35, 45],
    iconAnchor: [17, 46],
    popupAnchor: [0, -46],
});




// function LocationComponent({ setCenter }) {
//     const getLocation = () => {
//         if (navigator.geolocation) {
//             navigator.geolocation.getCurrentPosition(
//                 position => {
//                     const { latitude, longitude } = position.coords;
//                     setCenter({ lat: latitude, lng: longitude });
//                 },
//                 error => {
//                     console.error('Error getting location:', error.message);
//                 }
//             );
//         } else {
//             console.error('Geolocation is not supported by this browser.');
//         }
//     };

//     useEffect(() => {
//         getLocation();
//     }, []);

//     return null;
// }

function Maps() {
    const [center, setCenter] = useState({lat:28, lng:84 });
    const ZOOM = 9;
    const mapRef = useRef();
    const location = useGeoLocation();

    console.log("Center:", center);
    console.log("Bee data:", bees);  // Log the bee data to confirm it's loaded

    const showMyLocation = () => {
        if (location.loaded && !location.error) {
            mapRef.current.leafletElement.flyTo (
                [location.coordinates.lat, location.coordinates.lng],
                ZOOM,
                {animate: true}
            );
        }
        else {
            alert(location.error.message);
        }
        };
    return (
        <div className='container'>
            {/* <LocationComponent setCenter={setCenter} /> */}
            {center && (
                <MapContainer center={center} zoom={ZOOM} ref={mapRef}>
                    {location.loaded && !location.error && (
                        <Marker icon = {personIcon} position={[location.coordinates.lat, location.coordinates.lng]}></Marker>
                    )}
                    <TileLayer url={osm.maptiler.url} attribution={osm.maptiler.attribution} />
                    {bees.map((bee, idx) => (
                        <Marker key={idx} position={[bee.lat, bee.long]} icon={markerIcon}>
                            <Popup>
                                <b>{bee['bee-type']}</b><br/>
                                <img src={`../images/${bee['bee-image']}`} alt={bee['bee-type']} style={{width: '100px'}} /><br/>
                                Latitude: {bee.lat}, Longitude: {bee.long}
                            </Popup>
                        </Marker>
                    ))}
                </MapContainer>
            )}
            <button className='LocateButton' onClick={showMyLocation}>Locate Me</button>
        </div>
    );
}

export default Maps;
