import React, { useState, useRef, useEffect } from 'react';
import { MapContainer, Marker, Popup, TileLayer } from 'react-leaflet';
import osm from './osm-provider';
import "leaflet/dist/leaflet.css";
import L from "leaflet"

const markerIcon = new  L.Icon({
    iconUrl: './images/beeicon.png',
    iconSize: [35,45],
    iconAnchor: [17, 46],
    popupAnchor: [0, -46],
});
function LocationComponent({ setCenter }) {
    const getLocation = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                position => {
                    const { latitude, longitude } = position.coords;
                    console.log("Latitude:", latitude);
                    console.log("Longitude:", longitude);
                    setCenter({ lat: latitude, lng: longitude });
                },
                error => {
                    console.error('Error getting location:', error.message);
                }
            );
        } else {
            console.error('Geolocation is not supported by this browser.');
        }
    };

    // Call getLocation when the component mounts
    useEffect(() => {
        getLocation();
    }, []);

    return null; // LocationComponent doesn't render anything
}

function Maps() {
    const [center, setCenter] = useState(null);
    const ZOOM = 9;
    const mapRef = useRef();

    console.log("Center:", center);

    return (
        <div className='container'>
            <LocationComponent setCenter={setCenter} />
            {center && (
                <MapContainer center={center} zoom={ZOOM} ref={mapRef}>
                    <TileLayer url={osm.maptiler.url} attribution={osm.maptiler.attribution} />
                    <Marker position={[27.618921930328547, 85.53861010994964]} icon={markerIcon}>
                        <Popup>
                            <b>First Marker</b>
                        </Popup>
                    </Marker>
                </MapContainer>
            )}
        </div>
    );
}

export default Maps;
